INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_41053001', 'PHYSICAL', 'Ready to drink beverages, containing milk, formulated and labeled for their nutritional value, such as increased caloric or protein intake.', 'Nutritional Supplement, Protein Drinks, Shakes - Contains Milk');
