INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_50021001', 'SERVICES', 'Charges associated with recurring membership dues allowing access or use of health clubs and fitness clubs.  Outside of the United States, these charges are not applicable for services provided by non-profit sports organizations.', 'Fitness Centers - Dues and Membership Fees');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_50021002', 'SERVICES', 'Initiation fees are an upfront one-time charge occurring at the time a new member joins a health club or fitness center.  Outside of the United States, these charges are not applicable for services provided by non-profit sports organizations.', 'Fitness Centers - Initiation Fees');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_50021003', 'SERVICES', 'Charges associated with the service of providing in-person personal fitness training or group excercise classes.  Outside of the United States, these charges are not applicable for services provided by non-profit sports organizations.', 'Fee for Personal Training/Fitness Classes');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_50021101', 'SERVICES', 'Charges associated with one-time access for use of health clubs and fitness clubs.  Outside of the United States, these charges are not applicable for services provided by non-profit sports organizations.', 'Fitness Centers - Single Use Access Fees');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_50021103', 'SERVICES', 'Charges associated with the service of providing in-person instruction in an athletic pursuit, eg. golf, tennis, skiing, etc.  Outside of the United States, these charges are not applicable for services provided by non-profit sports organizations.', 'Fee For Sport Instruction Classes');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_90000001', 'PHYSICAL', 'A monetary donation for a cause, in which the donee receives nothing in return.', 'Cash Donation');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_90020001', 'SERVICES', 'Gratuity, otherwise known as a tip, is money that a customer leaves for an employee or employees that is in addition to and separate from the amount due for the goods or services rendered.', 'Optional Gratuity');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_92010001', 'PHYSICAL', 'A shipping charge for the delivery of physical goods in conjunction with the sale of these goods. This tax category is not appropriate for stand alone transportation charges that are not associated with the sale of the goods being delivered.', 'Shipping');