INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_99999999', 'PHYSICAL', 'A physical good that can be moved or touched. Also known as tangible personal property.', 'General - Tangible Goods');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_20030000', 'SERVICES', 'General category for services. It should be used only when there is no more specific services category. In the European Union, the default rule for business-to-consumer sales (B2C) is the location of the seller, whereas for business-to-business sales (B2B) - the location of the buyer.', 'General - Services');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_10000000', 'DIGITAL', 'A digital service provided mainly through the internet with minimal human involvement, relying on information technology. Consider more specific categories like software, digital goods, cloud services, or website services for your product (especially if you sell in the US). If you stay with this category, taxes will be similar to those for a generic digital item like downloaded music.', 'General - Electronically Supplied Services');
INSERT INTO product_taxes (id, type, description, name) VALUES ('txcd_00000000', 'PHYSICAL', 'Any nontaxable good or service which can be used to ensure no tax is applied, even for jurisdictions that impose a tax.', 'Nontaxable');